INSERT INTO `np_about` (`aid`, `type`, `title`, `content`) VALUES
(1, '公司简介', '公司简介', '<p>&nbsp;华智讯讲师合作平台</p><p>&nbsp; &nbsp; &nbsp; &nbsp; 华智讯讲师经济机构成立于2004年7月，立致于培养企业管理讲师，并将众多优秀讲师，推广至各培训渠道（咨询公司、培训公司），创业至今成功培养多名企业管理讲师，汇集业内优秀讲师1000余名，在业内极具知名度和影响力！主要合作伙伴包括：清华大学、北京大学、时代光化、北大纵横、正略君策、聚成、实践家……等国内著名培训机构！</p><p>&nbsp; &nbsp; &nbsp;员工的成长与职业发展是机构重要工作内容之一，因此机构会不断为员工提供学习与培训机会，提升员工工作能力与管理能力；机构会根据员工的能力与特长为员工设计职业发展路径，并提供更大的职业发展平台！</p><p><br/></p>'),
(2, '荣誉资质', '荣誉资质', '<p>荣誉资质<br /></p>'),
(3, '联系我们', '联系我们', '<p>地址：北京市.海淀区.上地东路.上地公寓405室</p><p>传真：010-62980272</p><p>电话：010-57167875</p><p><br/></p><p>联系人：</p><p>王冬梅：18001208951 &nbsp; &nbsp; &nbsp;QQ:869591145</p><p>李 &nbsp; &nbsp;静：13581514669 &nbsp; &nbsp; &nbsp;QQ:2373806745</p>'),
(4, '董事长简介', '董事长简介', '<p style="text-indent:2em;">董事长简介</p>'),
(5, '联系我们', '联系我们', '<p>地址：北京市.海淀区.上地东路.上地公寓405室</p><p>传真：010-62980272</p><p>电话：010-57167875</p><p><br/></p><p><br/></p><p>联系人：</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">王冬梅：18001208951 &nbsp; &nbsp; &nbsp;QQ:869591145</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; ">李 &nbsp; &nbsp;静：13581514669 &nbsp; &nbsp; &nbsp;QQ:2373806745</p><p><br/></p>');

