INSERT INTO `np_courses` (`id`, `type`, `name`, `photo`, `professor`, `content`, `add_time`, `index`) VALUES
(1, '能力提升与工作协作', '《由内而外的高效沟通》', '/upload/20130728/13749948276454.png', '辰辉', '', 1374988229, 0),
(2, '通用管理', '《非人力资源的人力资源管理》', '', '艾志朋', '', 1374988349, 0),
(3, '心态与职业技能提升', '《优秀员工是怎样炼成的》', '/upload/20130815/13765487404126.jpg', '艾志朋', '<p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:16px;font-family:宋体">【课程背景】</span></strong></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp;</span><span style="font-size:14px;font-family:宋体">公司投入大量的人财物培养的员工难以稳定；</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp;</span><span style="font-size:14px;font-family:宋体">新员工进入企业短期内难以适应；</span></p><p style=";text-indent:28px;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14px;font-family:宋体">员工总是情绪低落，效率低下，应付工作；</span></p><p style=";text-indent:28px;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14px;font-family:宋体">员工主动性差，总是喜欢相互推诿责任；</span></p><p style=";text-indent:28px;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14px;font-family:宋体">员工利已主义严重，团队一盘散沙；</span></p><p style=";text-indent:28px;line-height:23px"><span style="font-size:14px;font-family:宋体">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14px;font-family:宋体">员工不敬业，对工作缺少责任心；</span></p><p style="text-align:justify;text-justify:inter-ideograph;text-indent:27px;line-height:23px"><span style="font-size:14px;font-family:宋体">员工缺少主人公精神，总是有诸多抱怨，影响团队士气；</span></p><p style="text-align:justify;text-justify:inter-ideograph;text-indent:27px;line-height:23px"><span style="font-size:14px;font-family:宋体">…………</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:16px;font-family:宋体">【课程收益】：</span></strong></p><p style="text-indent:28px;line-height:23px"><span style="font-size:14px;font-family:宋体">了解职业角色认知和职场伦理，改变员工的心智模式，为员工缓解工作压力，培养感恩、积极和乐观的职业化心态，培养团队协作精神，提高员工工作的积极性、主动性，激发员工内在的工作动力，塑造自动自发和负责任的工作习惯和工作风格。</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:16px;font-family:宋体">【授课对象】</span></strong></p><p style="line-height:23px"><span style="font-size:14px;font-family:宋体;color:black">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14px;font-family:宋体;color:black">基层员工与储备干部</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:16px;font-family:宋体">【课程大纲】</span></strong></p><p><strong><span style="font-size:14px;font-family:宋体"><br clear="all" style="page-break-before:auto"/></span></strong></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第一部分：序言</span></strong></p><p style=";text-align:justify;text-justify:inter-ideograph;text-indent:0;line-height:23px"><span style="font-size:14px;font-family:宋体">1． </span><span style="font-size:14px;font-family:宋体">互动：你知道自己想要的是什么？</span></p><p style=";text-align:justify;text-justify:inter-ideograph;text-indent:0;line-height:23px"><span style="font-size:14px;font-family:宋体">2． </span><span style="font-size:14px;font-family:宋体">是什么让你如此的烦恼<br/>2． 人生两大主题：安居与乐业</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第二部分：职场伦理</span></strong></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">什么是职场伦理；</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">案例：职场伦理的缺失导致上级向下级汇报；</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">企业家眼中的职业化</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">职业人的特点</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第三部分：职场管住自己这张嘴</span></strong></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">案例：金佛的启示</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">不抱怨，不传是非——方法</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">不犟嘴，不争论——工具</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">不恶语，不毒言——方法</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">多赞美，多良言——工具</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第四部分：改善工作的心态</span></strong></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">案例讨论：渔夫的启示</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">快乐工作的动机来源于充足的理由</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">人的使命——爱与创造</span></p><p style="margin-left:24px;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">如何确立敬业的工作价值观</span></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">5.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">负责任的态度比技能更重要</span></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">6.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">职业化心态——专业、敬业、商业、乐业、事业、职业</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第五部分：改善工作的习惯</span></strong></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">改变时间管理的习惯——提高工作效率</span></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">改变上传下达的习惯——提高沟通力</span></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">改变拖拉习惯——提高执行力</span></p><p style="margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:14px;font-family:宋体">养成创新的习惯——提高绩效</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第六部分：在团队协作中提高影响力</span></strong></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">先天五德：功德、道德、福德、阴德、圣德</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">气运与气数、寿数之说</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">德高方能望重，感恩才能协作</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">马斯洛人生需求之爱与归属</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">5.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">团队协作的秘诀——搭台与拆台</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第七部分：提高学习力</span></strong></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">案例分享：讲学习、讲政治、讲正气</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">马斯洛的人生八大需求之求知的需求</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">什么是学习型组织</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">什么是行动学习</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">5.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">学习的方式和渠道、工具</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">6.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">学习型组织的打造</span></p><p style="text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style="font-size:14px;font-family:宋体">第八部分：职业生涯规划与发展</span></strong></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">1.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">修炼的定义：找到自身的缺陷并不断完善</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">2.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">江山易改，本性难移</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">3.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">改变心态：找到心态的缺陷并完善</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">4.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">改变行为：找到习惯的缺陷并完善</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">5.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">改变气运：找到气运的缺陷并完善</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">6.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">改变命格：找到命理的缺陷并完善</span></p><p style="margin-left:28px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style="font-size:14px;font-family:宋体">7.<span style="font:9px &#39;Times New Roman&#39;">&nbsp;&nbsp;</span></span><span style="font-size:14px;font-family:宋体">人生的过程就是不断修炼并逐步完善的过程，即为圆满。</span></p><p><br/></p>', 1374988449, 1),
(4, '管理能力提升', '《卓越领导力塑造》', '/upload/20130815/13765441506075.jpg', '杨帆', '<p style="margin-left:40px;line-height:150%;background:#F3F3F3"><strong><span style="font-size:19px;line-height:150%;font-family:幼圆;color:#292929">课程背景：</span></strong></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织工作效率低、相互扯皮推诿；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织成员拒绝学习、成长缓慢；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织成员不认同组织目标和愿景；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织成员抵触管理、不愿遵守制度；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织没有凝聚力、无法形成合力；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">组织文化不能落地，不能指导员工实践；</span></p><p style="margin-left:92px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">………………</span></p><p style="margin-left:28px;text-indent:32px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">一系列令领导者头疼的问题制约着组织发展与绩效成长，而问题背后反映出的则是管理者缺乏一套行之有效、适应本土企业现状的管理模式，《卓越领导力开发与高绩效团队建设》课程全程案例式学习，从人性、文化、管理、组织等多个方面对组织管理进行深入解析，并提供系统化解决方案：有模式、有机制、有工具、有具体使用方法与技巧，在提升您个人领导能力的同时，快速构建起组织领导能力，倍速提升团队绩效！</span></p><p style="margin-left:35px;line-height:150%"><strong><span style="font-size:16px;line-height:150%;font-family:幼圆;color:#292929">&nbsp;</span></strong></p><p style="margin-left:40px;line-height:150%;background:#F3F3F3"><strong><span style="font-size:19px;line-height:150%;font-family:幼圆;color:#292929">课程目标：</span></strong></p><p style="margin-left:56px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">找到制约组织效能的领导瓶颈；</span></p><p style="margin-left:56px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">找到传统领导模式的短板所在；</span></p><p style="margin-left:56px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;color:#292929">l<span style="font:9px &#39;Times New Roman&#39;">&nbsp; </span></span><span style="font-size:16px;line-height:150%;font-family:宋体;color:#292929">明确领导力养成路径与关键内容；</span></p><p style="margin-left:56px;line-height:150%"><span style="font-size:16px;line-height:150%;font-family:Wingdings;