INSERT INTO `np_teacher` (`id`, `name`, `photo`, `position`, `quality`, `intro`, `add_time`, `index`) VALUES
(1, '杨帆', '/upload/20130815/13765494055559.jpg', '企业管理实战专家', '北大经济学院   特聘讲师\r\n北大政府管理学院  特聘讲师\r\n清华职业经理人训练中心  特聘讲师', '<p style="margin-top:13px;margin-bottom:8px;text-align:left;line-height:150%"><strong><span style="font-size:19px;line-height:150%;font-family:幼圆"></span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 18px; "><strong><span style="font-size: 19px; line-height: 28px; font-family: 幼圆; ">金牌课程：</span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 18px; "><span style="font-size: 18px; line-height: 28px; font-family: 幼圆; ">《团队高效协作与执行管理模式》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 18px; "><span style="font-size: 18px; line-height: 28px; font-family: 幼圆; ">《卓越领导力塑造》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; "><span style="font-family:幼圆;font-size:3px"><span class="Apple-style-span" style="line-height: 28px;"><br/></span></span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 18px; "><strong><span style="font-size: 19px; line-height: 28px; font-family: 幼圆; ">擅长领域：</span></strong><br/></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 18px; "><span style="font-size: 16px; line-height: 24px; font-family: 宋体; ">&nbsp;&nbsp;&nbsp;</span><span style="line-height: 24px; font-family: 宋体; font-size: 18px; ">通用管理类&nbsp;&nbsp; 领导力与执行力&nbsp;&nbsp; 团队建设&nbsp;&nbsp; 班组建设</span></p><p style="margin-top:13px;margin-bottom:8px;text-align:left;line-height:150%"><strong><span style="font-size:19px;line-height:150%;font-family:幼圆"><br/></span></strong><strong><span style="font-size:19px;line-height:150%;font-family:幼圆">课程特点：</span></strong><strong><span style="font-size:19px;line-height:150%;font-family:幼圆"><br/></span></strong></p><p style="margin-left:30px;text-align:left;line-height:150%"><span style="line-height: 150%; font-family: 宋体; font-size: 18px; ">十余年营销与企业管理经历，七年管理咨询与培训专业积累，对于中外企业具体实施培训和咨询有着丰富的实践经验，并逐步形成了适宜于本土化管理的咨询培训风格，曾为百余家中外优秀企业提供过咨询培训服务工作，其具有针对性的、实战性的咨询培训方案和针对企业管理的独到见解得到了众多企业的认可和赞誉。</span></p><p style="line-height:150%"><strong><span style="font-size:16px;line-height:150%;font-family:幼圆">&nbsp;</span></strong></p><p><span class="Apple-style-span" style="line-height: 18px; "><strong><span style="font-size:19px;line-height:150%;font-family:幼圆">曾服务的部分企业：</span></strong></span><br/></p><p style="margin-left:24px;text-align:left;line-height:150%"><span style="line-height: 150%; font-family: 宋体; color: rgb(38, 38, 38); font-size: 18px; ">中粮、中船、中石化、中海油、中国黄金集团、中国工商银行、中国农业银行、浦发银行、中国移动、中国电信、中国联通、中钢、深圳海普瑞制药、神威药业、徐州重工、三一重工、中联重科、斗山机械、上海德尔福、现代汽车、北盛汽车、广州地铁、南京地铁、航天三院、华北空管局、首都机场、新疆机场、国航、国华电力、北京电力、成都电力、河北电力、中国核电研究院、中国水利水电集团、搜狐、中外运集运、白沙保烟、安徽中烟、辽宁红塔、蒙牛、汇源果汁、金星啤酒、崂山矿泉水、绝味食品、神州租车、恒安集团、中国采购招标网、四川电视台、河南永煤、神华宁煤………</span></p><p style="margin-left:24px;text-align:left;line-height:150%"><strong><span style="font-size:16px;line-height:150%;font-family:宋体;color:#333333">&nbsp;</span></strong></p><p><br/></p>', 1374985394, 2),
(4, '王大泓', '/upload/20130815/13765398222251.jpg', '企业管理实战专家', '曾于著名德资企业、国有企业和民营企业从事营销及高管，深谙中外管理差异和人性背后的管理哲学，其课程其具系统性与实战性，为学员提供大量具体的管理工具。', '<p style="line-height:115%"><strong><span style="font-size:21px;line-height:115%;font-family:微软雅黑"></span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 13px; "><strong><span style="font-size: 21px; line-height: 24px; font-family: 微软雅黑; "><strong><span style="font-size: 21px; line-height: 24px; font-family: 微软雅黑; ">金牌课程：</span></strong></span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; "><span style="font-size: 18px; line-height: 115%; font-family: 微软雅黑; color: black; ">&nbsp; &nbsp; &nbsp; &nbsp;《高绩效团队》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; "><span style="font-size: 18px; line-height: 115%; font-family: 微软雅黑; color: black; ">&nbsp; &nbsp; &nbsp; &nbsp;《卓越团队建设与管理》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; "><span style="font-size: 18px; line-height: 115%; font-family: 微软雅黑; color: black; ">&nbsp; &nbsp; &nbsp; &nbsp;《第五级班组建设》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; "><span style="font-size: 18px; line-height: 115%; font-family: 微软雅黑; color: black; ">&nbsp; &nbsp; &nbsp; &nbsp;《班组长胜任能力提升》</span></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 13px; "><strong><span style="font-size: 21px; line-height: 24px; font-family: 微软雅黑; "><br/></span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 13px; "><strong><span style="font-size: 21px; line-height: 24px; font-family: 微软雅黑; ">擅长领域：</span></strong></p><p style="margin-top: 5px; margin-right: 0px; margin-bottom: 5px; margin-left: 0px; line-height: 13px; "><span style="line-height: 20px; font-family: 微软雅